import base64

def __decrypt(encrypted, key):
    encrypted_bytes = base64.b64decode(encrypted.encode('utf-8'))
    return bytes([b ^ ord(key[i % len(key)]) for i, b in enumerate(encrypted_bytes)]).decode('utf-8')

